// src/components/Header.jsx
import { Link, useLocation } from "react-router-dom";
import { useEffect, useState } from "react";

const Header = () => {
  const location = useLocation();
  const [activeSection, setActiveSection] = useState("/");

  const sections = [
    { name: "Home", path: "/" },
    { name: "Crypto Trend", path: "/crypto-trend" },
    { name: "Video Gen", path: "/video-gen" },
    { name: "Image Gen", path: "/image-gen" },
  ];

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const thresholds = sections.map((section) => {
        const el = document.querySelector(`[data-section='${section.path}']`);
        return el ? { path: section.path, offset: el.offsetTop } : null;
      }).filter(Boolean);

      const current = thresholds.reverse().find((section) => scrollPosition >= section.offset - 100);
      if (current) {
        setActiveSection(current.path);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header className="fixed top-0 left-0 w-full z-50 backdrop-blur-lg bg-black/30 border-b border-white/10 shadow-xl">
      <nav className="flex justify-between items-center px-8 py-4">
        <h1 className="text-2xl font-bold text-white">Trend<span className="text-purple-400">AI</span></h1>
        <ul className="flex space-x-8">
          {sections.map((section) => (
            <li key={section.path}>
              <Link
                to={section.path}
                className={`text-sm md:text-base transition-all duration-300 ${
                  activeSection === section.path
                    ? "text-purple-400 font-semibold"
                    : "text-white/70 hover:text-purple-300"
                }`}
              >
                {section.name}
              </Link>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
