/* === src/App.jsx === */
import React, { useEffect, useRef, useState } from "react";
import Header from "./components/Header";
import Home from "./pages/Home";
import CryptoTrend from "./pages/CryptoTrend";
import VideoGen from "./pages/VideoGen";
import ImageGen from "./pages/ImageGen";
import "./index.css";

export default function App() {
  const homeRef = useRef(null);
  const cryptoRef = useRef(null);
  const videoRef = useRef(null);
  const imageRef = useRef(null);
  const [activeSection, setActiveSection] = useState("home");

  useEffect(() => {
    const handleScroll = () => {
      const sections = [
        { name: "home", ref: homeRef },
        { name: "crypto", ref: cryptoRef },
        { name: "video", ref: videoRef },
        { name: "image", ref: imageRef },
      ];

      const scrollPosition = window.scrollY + window.innerHeight / 2;
      const visibleSection = sections.find(
        (section) =>
          section.ref.current.offsetTop <= scrollPosition &&
          section.ref.current.offsetTop + section.ref.current.offsetHeight > scrollPosition
      );

      if (visibleSection) {
        setActiveSection(visibleSection.name);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="relative bg-black min-h-screen font-sans">
      <Header active={activeSection} />
      <section ref={homeRef} id="home" className="snap-start scroll-mt-20">
        <Home />
      </section>
      <section ref={cryptoRef} id="crypto" className="snap-start scroll-mt-20">
        <CryptoTrend />
      </section>
      <section ref={videoRef} id="video" className="snap-start scroll-mt-20">
        <VideoGen />
      </section>
      <section ref={imageRef} id="image" className="snap-start scroll-mt-20">
        <ImageGen />
      </section>
    </div>
  );
}
