import React, { useEffect, useState } from 'react';
import { Link as ScrollLink, Events } from 'react-scroll';

const sections = ['home', 'about', 'features', 'pricing', 'contact'];

const Header = () => {
  const [activeSection, setActiveSection] = useState('home');

  useEffect(() => {
    const handleScroll = () => {
      const scrollPos = window.scrollY + window.innerHeight / 2;

      for (let section of sections) {
        const el = document.getElementById(section);
        if (el) {
          const { top, height } = el.getBoundingClientRect();
          const offsetTop = el.offsetTop;
          if (scrollPos >= offsetTop && scrollPos < offsetTop + height) {
            setActiveSection(section);
            break;
          }
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    Events.scrollEvent.register('end', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
      Events.scrollEvent.remove('end');
    };
  }, []);

  return (
    <header className="fixed top-0 w-full z-50 backdrop-blur-xl bg-gradient-to-r from-[#0b0b0c]/80 to-[#141517]/80 border-b border-white/10 shadow-lg animate-glow transition-all duration-500">
      <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        <h1 className="text-white text-2xl font-bold tracking-wider">Trend<span className="text-cyan-400">AI</span></h1>
        <nav className="space-x-6 text-sm font-medium flex">
          {sections.map(section => (
            <ScrollLink
              key={section}
              to={section}
              spy={true}
              smooth={true}
              duration={500}
              offset={-70}
              className={`cursor-pointer transition-colors duration-300 uppercase tracking-wider ${
                activeSection === section
                  ? 'text-cyan-400'
                  : 'text-white/60 hover:text-white'
              }`}
            >
              {section}
            </ScrollLink>
          ))}
        </nav>
      </div>
    </header>
  );
};

export default Header;
