import React from 'react';
import { NavLink } from 'react-router-dom';

const links = [
  { name: 'Home', path: '/' },
  { name: 'Crypto Trend', path: '/crypto-trend' },
  { name: 'Video Gen', path: '/video-gen' },
  { name: 'Image Gen', path: '/image-gen' },
];

export default function Header() {
  return (
    <header className="sticky top-0 z-50 backdrop-blur-lg bg-black/40 border-b border-gray-700 shadow-sm">
      <nav className="max-w-7xl mx-auto flex items-center justify-between p-5">
        <div className="flex items-center space-x-3 select-none">
          <img
            src="/trendAiLogo.png"
            alt="TrendAI Logo"
            className="h-10 w-auto rounded-md shadow-md"
            style={{ backgroundColor: 'rgba(10,10,10,0.6)' }}
          />
          <h1 className="text-2xl font-semibold tracking-wide text-cyan-500">TrendAI</h1>
        </div>
        <ul className="flex space-x-8 text-lg font-medium">
          {links.map(({ name, path }) => (
            <li key={path}>
              <NavLink
                to={path}
                end
                className={({ isActive }) =>
                  isActive
                    ? 'text-cyan-500 border-b-2 border-cyan-500 pb-1 transition-colors duration-300'
                    : 'text-gray-400 hover:text-cyan-400 transition-colors duration-300'
                }
              >
                {name}
              </NavLink>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
}
