import React, { useEffect, useState } from "react";
import { Link as ScrollLink, Events } from "react-scroll";

const sections = ["home", "about", "features", "contact"];

const Header = () => {
  const [activeSection, setActiveSection] = useState("home");

  useEffect(() => {
    Events.scrollEvent.register("begin", () => {});
    Events.scrollEvent.register("end", () => {});

    const handleScroll = () => {
      const scrollY = window.scrollY;
      const sectionOffsets = sections.map((id) => {
        const el = document.getElementById(id);
        return {
          id,
          offsetTop: el?.offsetTop || 0,
          offsetHeight: el?.offsetHeight || 0,
        };
      });

      const current = sectionOffsets.find(
        (section) =>
          scrollY >= section.offsetTop - 100 &&
          scrollY < section.offsetTop + section.offsetHeight - 100
      );

      if (current) {
        setActiveSection(current.id);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header
      className="fixed top-0 left-0 w-full z-50 backdrop-blur-md bg-black/50 shadow-lg border-b border-white/10 transition-all duration-500"
      style={{
        boxShadow: "0 0 30px rgba(0, 255, 255, 0.05)",
        background: "linear-gradient(145deg, rgba(20, 20, 20, 0.6), rgba(10, 10, 10, 0.8))",
      }}
    >
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        <h1 className="text-white font-semibold text-xl tracking-widest">TrendAI</h1>
        <nav className="flex space-x-8">
          {sections.map((section) => (
            <ScrollLink
              key={section}
              to={section}
              spy={true}
              smooth={true}
              duration={500}
              offset={-60}
              className={`cursor-pointer transition-colors duration-300 uppercase tracking-wide text-sm font-medium hover:text-cyan-400 ${
                activeSection === section ? "text-cyan-400" : "text-gray-400"
              }`}
            >
              {section}
            </ScrollLink>
          ))}
        </nav>
      </div>
      {/* Shine effect */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute -top-1 left-0 w-full h-0.5 bg-gradient-to-r from-transparent via-cyan-300/30 to-transparent animate-shimmer" />
      </div>
    </header>
  );
};

export default Header;
