// App.jsx
import React, { useEffect, useRef, useState } from 'react';
import Header from './components/Header';
import Home from './pages/Home';
import CryptoTrend from './pages/CryptoTrend';
import VideoGen from './pages/VideoGen';
import ImageGen from './pages/ImageGen';

function App() {
  const [activeSection, setActiveSection] = useState('home');

  const homeRef = useRef(null);
  const cryptoRef = useRef(null);
  const videoRef = useRef(null);
  const imageRef = useRef(null);

  const handleScroll = () => {
    const sections = [
      { id: 'home', ref: homeRef },
      { id: 'crypto', ref: cryptoRef },
      { id: 'video', ref: videoRef },
      { id: 'image', ref: imageRef },
    ];

    const scrollPosition = window.scrollY + window.innerHeight / 2;

    for (let section of sections) {
      const offsetTop = section.ref.current.offsetTop;
      const offsetHeight = section.ref.current.offsetHeight;
      if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
        setActiveSection(section.id);
        break;
      }
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="bg-black text-white font-sans">
      <Header activeSection={activeSection} />
      <div ref={homeRef} id="home">
        <Home />
      </div>
      <div ref={cryptoRef} id="crypto">
        <CryptoTrend />
      </div>
      <div ref={videoRef} id="video">
        <VideoGen />
      </div>
      <div ref={imageRef} id="image">
        <ImageGen />
      </div>
    </div>
  );
}

export default App;
