/* === src/components/Header.jsx === */
import React from "react";
import { Link } from "react-scroll";

export default function Header({ active }) {
  const sections = [
    { id: "home", label: "Home" },
    { id: "crypto", label: "Crypto Trend" },
    { id: "video", label: "Video Gen" },
    { id: "image", label: "Image Gen" },
  ];

  const getBgColor = () => {
    switch (active) {
      case "home":
        return "bg-black/50";
      case "crypto":
        return "bg-gradient-to-r from-gray-900 via-black to-gray-900/80";
      case "video":
        return "bg-gradient-to-r from-zinc-800 via-neutral-900 to-zinc-800/80";
      case "image":
        return "bg-gradient-to-r from-slate-900 via-black to-slate-900/80";
      default:
        return "bg-black/40";
    }
  };

  return (
    <header
      className={`fixed top-0 left-0 w-full z-50 transition-all duration-700 backdrop-blur-md border-b border-white/10 shadow-lg ${getBgColor()}`}
    >
      <nav className="flex justify-between items-center px-6 py-3">
        <h1 className="text-white text-xl font-semibold tracking-widest">Trend<span className="text-purple-400">AI</span></h1>
        <div className="space-x-4 text-sm">
          {sections.map((section) => (
            <Link
              key={section.id}
              to={section.id}
              smooth={true}
              duration={700}
              offset={-80}
              className={`cursor-pointer px-3 py-1 rounded transition-all duration-300 text-white hover:text-purple-400 ${
                active === section.id ? "text-purple-400" : ""
              }`}
            >
              {section.label}
            </Link>
          ))}
        </div>
      </nav>
    </header>
  );
}
