import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";

const Header = () => {
  const location = useLocation();
  const [activeSection, setActiveSection] = useState("");

  const sections = [
    { name: "Home", path: "/" },
    { name: "Crypto Trend", path: "/cryptotrend" },
    { name: "Video Gen", path: "/videogen" },
    { name: "Image Gen", path: "/imagegen" },
  ];

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const sectionElements = sections.map(({ name }) =>
        document.getElementById(name.replace(/\s+/g, "").toLowerCase())
      );

      for (let i = sectionElements.length - 1; i >= 0; i--) {
        const el = sectionElements[i];
        if (el && el.offsetTop <= scrollY + 100) {
          setActiveSection(sections[i].path);
          break;
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header
      className="fixed top-0 w-full z-50 backdrop-blur-xl bg-black/30 border-b border-white/10 transition duration-500"
    >
      <nav className="flex items-center justify-between px-10 py-4">
        <h1 className="text-3xl font-bold text-white tracking-wide drop-shadow-glow">
          Trend<span className="text-cyan-400">AI</span>
        </h1>
        <ul className="flex gap-6 text-white font-medium text-sm">
          {sections.map(({ name, path }) => (
            <li key={path}>
              <Link
                to={path}
                className={`hover:text-cyan-300 transition duration-300 ${
                  location.pathname === path || activeSection === path
                    ? "text-cyan-400 font-semibold"
                    : ""
                }`}
              >
                {name}
              </Link>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
