// src/components/Header.jsx
import React, { useState, useEffect } from "react";
import { Link as ScrollLink } from "react-scroll";

const Header = () => {
  const [activeSection, setActiveSection] = useState("home");

  useEffect(() => {
    const handleScroll = () => {
      const sections = document.querySelectorAll("section");
      let current = "home";

      sections.forEach((section) => {
        const sectionTop = section.offsetTop - 200;
        const sectionHeight = section.clientHeight;
        if (window.scrollY >= sectionTop && window.scrollY < sectionTop + sectionHeight) {
          current = section.getAttribute("id");
        }
      });

      setActiveSection(current);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header className="fixed top-0 w-full z-50 backdrop-blur-lg bg-black/40 shadow-xl transition-all duration-500">
      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
        <div className="text-2xl font-bold text-white tracking-wider">TrendAI</div>
        <nav className="flex space-x-6">
          {[
            { id: "home", label: "Home" },
            { id: "crypto", label: "Crypto Trend" },
            { id: "video", label: "Video Gen" },
            { id: "image", label: "Image Gen" },
          ].map(({ id, label }) => (
            <ScrollLink
              key={id}
              to={id}
              spy={true}
              smooth={true}
              duration={500}
              className={`cursor-pointer text-lg font-medium transition-colors duration-300 ${
                activeSection === id ? "text-cyan-400" : "text-gray-300 hover:text-white"
              }`}
            >
              {label}
            </ScrollLink>
          ))}
        </nav>
      </div>
    </header>
  );
};

export default Header;
