// src/pages/VideoGen.jsx
import React, { useState } from "react";

const VideoGen = () => {
  const [category, setCategory] = useState("");
  const [style, setStyle] = useState("");
  const [prompt, setPrompt] = useState("");

  const categories = ["Crypto News", "AI Insights", "DeFi Explainers", "Market Predictions"];
  const styles = ["Animated", "Ultra Realistic", "Minimalist", "Futuristic"];

  return (
    <section id="videogen" className="min-h-screen flex items-center justify-center px-6 py-20 bg-transparent text-white">
      <div className="w-full max-w-5xl bg-white/5 backdrop-blur-md rounded-2xl shadow-2xl p-8 border border-white/10">
        <h2 className="text-3xl font-bold mb-6 text-center text-white/90">🎥 Generate Your Crypto Video</h2>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <div>
            <label className="block mb-2 text-sm font-medium text-white/70">Select Video Category</label>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              className="w-full p-3 rounded-lg bg-black/40 text-white border border-white/10 focus:ring-2 focus:ring-blue-600 transition"
            >
              <option value="">Choose a category</option>
              {categories.map((cat) => (
                <option key={cat}>{cat}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block mb-2 text-sm font-medium text-white/70">Select Style</label>
            <select
              value={style}
              onChange={(e) => setStyle(e.target.value)}
              className="w-full p-3 rounded-lg bg-black/40 text-white border border-white/10 focus:ring-2 focus:ring-blue-600 transition"
            >
              <option value="">Choose a style</option>
              {styles.map((style) => (
                <option key={style}>{style}</option>
              ))}
            </select>
          </div>
        </div>

        <div className="mb-6">
          <label className="block mb-2 text-sm font-medium text-white/70">Prompt (optional)</label>
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            rows="3"
            className="w-full p-3 rounded-lg bg-black/40 text-white border border-white/10 focus:ring-2 focus:ring-blue-600 transition resize-none"
            placeholder="Describe what you want in the video..."
          />
        </div>

        <div className="mb-6">
          <label className="block mb-2 text-sm font-medium text-white/70">Top Crypto Trends of the Day</label>
          <div className="p-4 rounded-lg bg-gradient-to-r from-black/40 to-black/30 border border-white/10 text-white/80">
            <ul className="space-y-1 list-disc pl-5">
              <li>Bitcoin ETF News</li>
              <li>AI-Powered Trading Bots</li>
              <li>Solana Ecosystem Growth</li>
              <li>Crypto Regulations Update</li>
            </ul>
          </div>
        </div>

        <div className="mb-8">
          <label className="block mb-2 text-sm font-medium text-white/70">Generated Video</label>
          <div className="h-60 bg-black/60 rounded-lg border border-white/10 flex items-center justify-center text-white/50 text-lg">
            [Your video will appear here...]
          </div>
        </div>

        <div className="text-center">
          <button className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-8 py-3 rounded-xl font-semibold shadow-lg hover:brightness-110 transition-all duration-300">
            ⬇️ Download Video
          </button>
        </div>
      </div>
    </section>
  );
};

export default VideoGen;
