import { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fix for marker icons not showing
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.9.3/dist/images/marker-shadow.png",
});

const countryCoordinates = {
  US: [37.0902, -95.7129],
  JP: [36.2048, 138.2529],
  DE: [51.1657, 10.4515],
  // Add more ISO codes if needed
};

const CryptoMap = () => {
  const [trendData, setTrendData] = useState([]);

  useEffect(() => {
    fetch("/trends.json")
      .then((res) => res.json())
      .then((data) => setTrendData(data))
      .catch((err) => console.error("Failed to fetch trend data", err));
  }, []);

  return (
    <div style={{ height: "500px", width: "100%" }}>
      <MapContainer center={[20, 0]} zoom={2} style={{ height: "100%", width: "100%" }}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a>'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {trendData.map((trend, index) => {
          const coords = countryCoordinates[trend.isoCode];
          if (!coords) return null;

          return (
            <Marker key={index} position={coords}>
              <Popup>
                <strong>{trend.country}</strong><br />
                Coin: {trend.coin}<br />
                Platform: {trend.platform}<br />
                Mentions: {trend.mentions}<br />
                Sentiment: {trend.sentiment > 0 ? "ðŸ˜Š" : "ðŸ˜¢"} ({trend.sentiment})<br />
                <em>{trend.snippet}</em><br />
                <img src={trend.top_meme} alt="Top meme" width="100%" /><br />
                <video width="100%" controls>
                  <source src={trend.top_video} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
              </Popup>
            </Marker>
          );
        })}
      </MapContainer>
    </div>
  );
};

export default CryptoMap;
