import React, { useState } from "react";
import Loading from "./Loading";

function VideoGenerator() {
  const [prompt, setPrompt] = useState("");
  const [loading, setLoading] = useState(false);
  const [videoUrl, setVideoUrl] = useState("");

  const handleGenerate = async () => {
    setLoading(true);
    setVideoUrl("");

    setTimeout(() => {
      setVideoUrl("https://www.example.com/video.mp4"); // demo
      setLoading(false);
    }, 3000);
  };

  return (
    <div className="glass-card w-full max-w-3xl">
      <h2 className="text-xl mb-4 font-semibold">Generate Video from Prompt</h2>
      <input
        type="text"
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        className="w-full px-4 py-2 rounded-md bg-white bg-opacity-10 backdrop-blur-md placeholder-white focus:outline-none focus:ring-2 focus:ring-purple-500"
        placeholder="Enter your prompt..."
      />
      <button
        onClick={handleGenerate}
        className="mt-4 px-6 py-2 bg-purple-600 hover:bg-purple-700 rounded-lg shadow-lg transition duration-300"
      >
        Generate
      </button>

      {loading && <Loading />}

      {videoUrl && (
        <video controls className="mt-6 rounded-xl w-full">
          <source src={videoUrl} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      )}
    </div>
  );
}

export default VideoGenerator;
