// Header.jsx
import React from 'react';

const Header = ({ activeSection }) => {
  const scrollTo = (id) => {
    document.getElementById(id)?.scrollIntoView({ behavior: 'smooth' });
  };

  const getHeaderColor = () => {
    switch (activeSection) {
      case 'home':
        return 'bg-black/40 backdrop-blur border-b border-gray-800';
      case 'crypto':
        return 'bg-zinc-900/40 backdrop-blur border-b border-zinc-800';
      case 'video':
        return 'bg-gray-900/40 backdrop-blur border-b border-gray-700';
      case 'image':
        return 'bg-slate-900/40 backdrop-blur border-b border-slate-700';
      default:
        return 'bg-black/30';
    }
  };

  return (
    <header className={`fixed top-0 left-0 w-full z-50 transition-colors duration-700 ease-in-out ${getHeaderColor()}`}>      
      <nav className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center text-white">
        <h1 className="text-2xl font-bold tracking-wide">TrendAI</h1>
        <ul className="flex gap-6 text-sm font-semibold cursor-pointer">
          <li onClick={() => scrollTo('home')} className="hover:opacity-80 transition">Home</li>
          <li onClick={() => scrollTo('crypto')} className="hover:opacity-80 transition">Crypto Trend</li>
          <li onClick={() => scrollTo('video')} className="hover:opacity-80 transition">Video Gen</li>
          <li onClick={() => scrollTo('image')} className="hover:opacity-80 transition">Image Gen</li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
