import React from "react";
import "./Banner3DSlider.css";

/**
 * images can be strings (img/video paths) or objects:
 *   { src, type?, poster? }
 * - type is optional; if omitted, we auto-detect by file extension
 */
export default function Banner3DSlider({
  images = [],
  title = "",
  subtitle = "",
  authorTitle = "",
  authorRole = "",
  authorText = "",
  modelImage = null,       // legacy silhouette (optional)
  centerLogo = null,       // NEW: static centered logo (e.g. "/images/trendai_logo.png")
}) {
  const showContent =
    Boolean(title || subtitle || authorTitle || authorRole || authorText || modelImage);

  // Normalize items
  const items = images.map((entry) =>
    typeof entry === "string" ? { src: entry } : entry
  );

  return (
    <div className="banner relative">
      {/* Static centerpiece logo (does not rotate) */}
      {centerLogo && (
        <div className="centerpiece" aria-hidden="true">
          <img src={centerLogo} alt="TrendAI logo" className="centerpiece-img" />
        </div>
      )}

      {/* 3D rotating ring */}
      <div className="slider" style={{ "--quantity": items.length }}>
        {items.map((item, i) => {
          const isVideo =
            item.type === "video" || /\.(mp4|webm|ogg)$/i.test(item.src);
          return (
            <div className="item" key={i} style={{ "--position": i + 1 }}>
              {isVideo ? (
                <video
                  className="media"
                  src={item.src}
                  poster={item.poster}
                  playsInline
                  muted
                  loop
                  autoPlay
                  preload="metadata"
                />
              ) : (
                <img className="media" src={item.src} alt={`slide-${i + 1}`} />
              )}
            </div>
          );
        })}
      </div>

      {/* Optional content layer (you can keep empty strings to hide) */}
      {showContent && (
        <div className="content">
          <h1 data-content={title}>{title}</h1>
          <div className="author">
            <h2>{authorTitle}</h2>
            <p><b>{authorRole}</b></p>
            <p>{authorText}</p>
            <p style={{ marginTop: 10, opacity: 0.8 }}>{subtitle}</p>
          </div>
          {!!modelImage && (
            <div className="model" style={{ backgroundImage: `url(${modelImage})` }} />
          )}
        </div>
      )}
    </div>
  );
}
