import React, { useEffect, useRef, useState } from 'react';
import Header from './components/Header';
import './index.css';

import Home from './pages/Home';
import CryptoTrend from './pages/CryptoTrend';
import VideoGen from './pages/VideoGen';
import ImageGen from './pages/ImageGen';

export default function App() {
  const [activeSection, setActiveSection] = useState('home');

  const homeRef = useRef(null);
  const cryptoRef = useRef(null);
  const videoRef = useRef(null);
  const imageRef = useRef(null);

  useEffect(() => {
    const sectionMap = {
      home: homeRef,
      crypto: cryptoRef,
      video: videoRef,
      image: imageRef,
    };

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const id = entry.target.id;
            setActiveSection(id);
          }
        });
      },
      { threshold: 0.6 }
    );

    Object.values(sectionMap).forEach((ref) => {
      if (ref.current) observer.observe(ref.current);
    });

    return () => {
      Object.values(sectionMap).forEach((ref) => {
        if (ref.current) observer.unobserve(ref.current);
      });
    };
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-black via-gray-900 to-gray-800 text-gray-200 font-sans selection:bg-cyan-600 selection:text-white overflow-x-hidden">
      <Header activeSection={activeSection} />
      <main className="max-w-7xl mx-auto space-y-40 px-6 scroll-smooth">
        <section id="home" ref={homeRef}><Home /></section>
        <section id="crypto" ref={cryptoRef}><CryptoTrend /></section>
        <section id="video" ref={videoRef}><VideoGen /></section>
        <section id="image" ref={imageRef}><ImageGen /></section>
      </main>
    </div>
  );
}
