import React from "react";
import { ComposableMap, Geographies, Geography } from "react-simple-maps";

const geoUrl =
  "https://raw.githubusercontent.com/deldersveld/topojson/master/world-countries.json";

const trendingCountries = {
  US: { coin: "BTC", platform: "X", snippet: "üöÄ Bitcoin on the rise!" },
  IN: { coin: "SHIBA", platform: "TikTok", snippet: "üî• Shiba memes trending" },
  DE: { coin: "ETH", platform: "X", snippet: "Ethereum is booming" },
};

const CryptoMap = () => {
  return (
    <div className="mt-20 bg-white/5 p-6 rounded-2xl backdrop-blur-md shadow-xl">
      <h2 className="text-2xl font-bold text-cyan-300 mb-4 text-center">
        üåç Trending Crypto by Region
      </h2>
      <p className="text-center text-sm text-gray-400 mb-4">
        Hover to see what‚Äôs trending where ‚Äì platform + coin + meme snippet.
      </p>
      <ComposableMap
        projection="geoEqualEarth"
        style={{ width: "100%", height: "auto" }}
      >
        <Geographies geography={geoUrl}>
          {({ geographies }) =>
            geographies.map((geo) => {
              const isoCode = geo.properties.ISO_A2;
              const trending = trendingCountries[isoCode];
              return (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  fill={trending ? "#00FFFF" : "#1f2937"}
                  stroke="#0e7490"
                  onMouseEnter={() => {
                    if (trending) {
                      const { coin, platform, snippet } = trending;
                      document.getElementById(
                        "tooltip"
                      ).innerHTML = `<strong>${geo.properties.NAME}</strong><br />${coin} on ${platform}<br /><em>${snippet}</em>`;
                    } else {
                      document.getElementById("tooltip").innerHTML =
                        geo.properties.NAME;
                    }
                  }}
                  onMouseLeave={() => {
                    document.getElementById("tooltip").innerHTML = "";
                  }}
                  style={{
                    default: { outline: "none" },
                    hover: { fill: "#06b6d4", outline: "none" },
                    pressed: { fill: "#0ea5e9", outline: "none" },
                  }}
                />
              );
            })
          }
        </Geographies>
      </ComposableMap>
      <div
        id="tooltip"
        className="text-xs text-white mt-4 text-center min-h-[40px]"
      ></div>
    </div>
  );
};

export default CryptoMap;
