import React from "react";
import "./Banner3DSlider.css";

export default function Banner3DSlider({
  images = [],
  title = "TREND AI",
  subtitle = "Fresh insights. Faster decisions.",
  authorTitle = "TrendAI",
  authorRole = "AI Crypto Insights",
  authorText = "Track AI tokens, spot momentum, and generate posts with agents.",
  modelImage = "/images/model.png" // optional background figure
}) {
  return (
    <div className="banner relative">
      {/* 3D Slider */}
      <div
        className="slider"
        style={{ "--quantity": images.length }}
      >
        {images.map((src, i) => (
          <div className="item" key={i} style={{ "--position": i + 1 }}>
            <img src={src} alt={`slide-${i + 1}`} />
          </div>
        ))}
      </div>

      {/* Content */}
      <div className="content">
        <h1 data-content={title}>{title}</h1>
        <div className="author">
          <h2>{authorTitle}</h2>
          <p><b>{authorRole}</b></p>
          <p>{authorText}</p>
          <p style={{ marginTop: 10, opacity: 0.8 }}>{subtitle}</p>
        </div>
        {!!modelImage && <div className="model" style={{ backgroundImage: `url(${modelImage})` }} />}
      </div>
    </div>
  );
}
