import React, { useEffect, useState } from "react";

const Header = () => {
  const [activeSection, setActiveSection] = useState("home");

  useEffect(() => {
    const sections = document.querySelectorAll("section");
    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            setActiveSection(entry.target.id);
          }
        });
      },
      { threshold: 0.6 }
    );

    sections.forEach(section => observer.observe(section));
    return () => observer.disconnect();
  }, []);

  const navItems = [
    { id: "home", label: "Home" },
    { id: "trends", label: "Crypto Trends" },
    { id: "video", label: "Video Generator" },
    { id: "image", label: "Image Generator" },
  ];

  return (
    <header className="fixed top-0 left-0 w-full z-50 backdrop-blur-md bg-black/30 border-b border-white/10 shadow-lg transition-all duration-500">
      <div className="max-w-7xl mx-auto flex justify-between items-center px-6 py-3">
        <div className="text-2xl font-bold text-white tracking-wide">TrendAI</div>
        <nav className="flex space-x-6">
          {navItems.map(item => (
            <a
              key={item.id}
              href={`#${item.id}`}
              className={`text-sm md:text-base font-medium transition-colors duration-300 ${
                activeSection === item.id
                  ? "text-purple-400"
                  : "text-gray-300 hover:text-white"
              }`}
            >
              {item.label}
            </a>
          ))}
        </nav>
      </div>
    </header>
  );
};

export default Header;
