import { useState } from 'react';
import Loading from './Loading';

export default function VideoGenerator() {
  const [loading, setLoading] = useState(false);
  const [videoUrl, setVideoUrl] = useState(null);

  const handleGenerate = () => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setVideoUrl("https://samplelib.com/lib/preview/mp4/sample-5s.mp4");
    }, 2000);
  };

  return (
    <div className="glass">
      <h2 className="text-xl font-semibold mb-4 text-purple-300">AI Video Generator</h2>
      <button
        onClick={handleGenerate}
        className="bg-purple-500 hover:bg-purple-600 text-white font-medium py-2 px-4 rounded-xl shadow-md"
      >
        Generate Video
      </button>
      {loading && <Loading />}
      {videoUrl && (
        <video src={videoUrl} controls className="mt-4 w-full rounded-xl shadow-xl"></video>
      )}
    </div>
  );
}
