import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix default icon paths for React
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const countryCoordinates = {
  US: [37.0902, -95.7129],
  JP: [36.2048, 138.2529],
  DE: [51.1657, 10.4515],
  BR: [-14.235, -51.9253],
  KR: [35.9078, 127.7669],
  FR: [46.6034, 1.8883]
};

const colorByTrend = {
  positive: 'green',
  neutral: 'yellow',
  negative: 'red'
};

const CryptoMap = () => {
  const [trends, setTrends] = useState([]);

  useEffect(() => {
    fetch('/trendData.json')
      .then(res => res.json())
      .then(data => setTrends(data))
      .catch(err => console.error('Failed to load trend data:', err));
  }, []);

  return (
    <div className="crypto-map-container">
      <h2 className="map-title">üåç Trending Crypto Globally</h2>

      <div className="map-wrapper">
        <MapContainer center={[20, 0]} zoom={2} style={{ height: '100%', width: '100%' }} scrollWheelZoom={true}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />

          {trends.map((trend, idx) => {
            const pos = countryCoordinates[trend.isoCode];
            if (!pos) return null;

            return (
              <Marker
                key={idx}
                position={pos}
                // Change pin color via a colored circle around the default pin
                // since default icon can't change color easily without files
                icon={new L.DivIcon({
                  html: `<div style="background-color: ${colorByTrend[trend.trend_label] || 'gray'}; 
                                width: 16px; height: 16px; border-radius: 50%; 
                                border: 2px solid white; box-shadow: 0 0 6px ${colorByTrend[trend.trend_label] || 'gray'};"></div>`,
                  className: '',
                  iconSize: [20, 20],
                  iconAnchor: [10, 10],
                  popupAnchor: [0, -10]
                })}
              >
                <Popup>
                  <strong>{trend.coin}</strong> from {trend.country}<br />
                  Platform: {trend.platform}<br />
                  Likes: {trend.likes}<br />
                  Trend: {trend.trend_label}<br />
                  Hashtags: <br />
                  {trend.hashtags.map((tag, i) => (
                    <span key={i} style={{ marginRight: 6, color: '#00ffff' }}>{tag}</span>
                  ))}
                </Popup>
              </Marker>
            );
          })}
        </MapContainer>
      </div>
    </div>
  );
};

export default CryptoMap;
