import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

const CryptoMap = () => {
  const [trends, setTrends] = useState([]);

  useEffect(() => {
    fetch('/trendData.json')
      .then(res => res.json())
      .then(data => setTrends(data))
      .catch(err => console.error('Failed to load trend data:', err));
  }, []);

  // Dummy country coordinates
  const countryCoordinates = {
    US: [37.0902, -95.7129],
    JP: [36.2048, 138.2529],
    DE: [51.1657, 10.4515]
  };

  return (
    <div className="crypto-map-container">
      <h2 className="map-title">üåç Trending Crypto Globally</h2>
      <div className="map-wrapper">
        <MapContainer center={[20, 0]} zoom={2} style={{ height: '100%', width: '100%' }}>
          <TileLayer
            url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
            attribution='&copy; OpenStreetMap contributors'
          />

          {trends.map((trend, idx) => (
            <Marker
              key={idx}
              position={countryCoordinates[trend.isoCode]}
              icon={L.icon({
                iconUrl: '/marker-icon.png',
                iconSize: [25, 41],
                iconAnchor: [12, 41],
                popupAnchor: [1, -34]
              })}
            >
              <Popup>
                <strong>{trend.coin}</strong> on {trend.platform}<br />
                Sentiment: {trend.sentiment > 0 ? 'üìà Positive' : 'üìâ Negative'}<br />
                Mentions: {trend.mentions}<br />
                <em>{trend.snippet}</em><br />
                <img src={trend.top_meme} alt="meme" width="100%" style={{ marginTop: '10px' }} /><br />
                <video src={trend.top_video} controls width="100%" style={{ marginTop: '10px' }} />
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>
    </div>
  );
};

export default CryptoMap;
