import React from 'react';
import { NavLink } from 'react-router-dom';

const links = [
  { name: 'Home', path: '/' },
  { name: 'Crypto Trend', path: '/crypto-trend' },
  { name: 'Video Gen', path: '/video-gen' },
  { name: 'Image Gen', path: '/image-gen' },
];

export default function Header() {
  return (
    <header className="sticky top-0 z-50 backdrop-blur-md bg-black/40 shadow-lg border-b border-cyan-600/30">
      <nav className="max-w-7xl mx-auto flex items-center justify-between p-4">
        <div className="flex items-center space-x-4 select-none">
          <img src="/trendAiLogo.png" alt="TrendAI Logo" className="h-10 w-auto drop-shadow-cyan" />
          <h1 className="text-2xl font-extrabold tracking-widest text-cyan-400 drop-shadow-neon">
            TrendAI
          </h1>
        </div>
        <ul className="flex space-x-8 text-lg font-medium">
          {links.map(({ name, path }) => (
            <li key={path}>
              <NavLink
                to={path}
                end
                className={({ isActive }) =>
                  isActive
                    ? 'text-cyan-400 border-b-2 border-cyan-400 pb-1 transition-all duration-300 drop-shadow-neon'
                    : 'text-gray-400 hover:text-cyan-300 transition-colors duration-300'
                }
              >
                {name}
              </NavLink>
            </li>
          ))}
        </ul>
      </nav>
    </header>
  );
}
