import React, { useEffect, useState, useRef } from 'react';
import Header from './components/Header';
import Home from './pages/Home';
import CryptoTrend from './pages/CryptoTrend';
import VideoGen from './pages/VideoGen';
import ImageGen from './pages/ImageGen';
import './index.css';

const App = () => {
  const [activeSection, setActiveSection] = useState('home');

  const homeRef = useRef(null);
  const cryptoRef = useRef(null);
  const videoRef = useRef(null);
  const imageRef = useRef(null);

  useEffect(() => {
    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0.6,
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          setActiveSection(entry.target.id);
        }
      });
    }, options);

    const sections = [homeRef, cryptoRef, videoRef, imageRef];
    sections.forEach(ref => {
      if (ref.current) {
        observer.observe(ref.current);
      }
    });

    return () => {
      sections.forEach(ref => {
        if (ref.current) {
          observer.unobserve(ref.current);
        }
      });
    };
  }, []);

  return (
    <div className="min-h-screen overflow-x-hidden bg-black">
      <Header activeSection={activeSection} />
      <div className="scroll-container">
        <section id="home" ref={homeRef} className="min-h-screen pt-[80px] transition-all duration-1000">
          <Home />
        </section>
        <section id="crypto" ref={cryptoRef} className="min-h-screen transition-all duration-1000">
          <CryptoTrend />
        </section>
        <section id="video" ref={videoRef} className="min-h-screen transition-all duration-1000">
          <VideoGen />
        </section>
        <section id="image" ref={imageRef} className="min-h-screen transition-all duration-1000">
          <ImageGen />
        </section>
      </div>
    </div>
  );
};

export default App;
