// TrendMap.jsx
import React from "react";
import { ComposableMap, Geographies, Geography, Marker } from "react-simple-maps";
import { motion } from "framer-motion";
import ReactTooltip from "react-tooltip";
import worldData from "../data/world-110m.json"; // Download TopoJSON

const trends = [
  {
    country: "USA",
    coordinates: [-95, 37],
    coin: "DOGE",
    platform: "TikTok",
    meme: "/memes/doge1.png",
    summary: "DOGE is trending due to Elon‚Äôs new post.",
  },
  {
    country: "Japan",
    coordinates: [139.6917, 35.6895],
    coin: "XRP",
    platform: "X",
    meme: "/memes/xrpJapan.png",
    summary: "XRP court drama getting attention.",
  },
];

const TrendMap = () => {
  return (
    <div className="bg-[#0d0d0d] rounded-2xl p-4 shadow-xl border border-[#202020]">
      <h2 className="text-xl font-bold text-lime-400 mb-4">üåç Global Crypto Trends</h2>
      <ComposableMap projectionConfig={{ scale: 150 }}>
        <Geographies geography={worldData}>
          {({ geographies }) =>
            geographies.map(geo => (
              <Geography
                key={geo.rsmKey}
                geography={geo}
                style={{
                  default: { fill: "#1f1f1f", outline: "none" },
                  hover: { fill: "#444444", outline: "none" },
                  pressed: { fill: "#0f0f0f", outline: "none" },
                }}
              />
            ))
          }
        </Geographies>

        {trends.map((trend, i) => (
          <Marker key={i} coordinates={trend.coordinates}>
            <motion.circle
              r={6}
              fill={trend.platform === "TikTok" ? "#00f2ea" : "#1DA1F2"}
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: i * 0.2 }}
              data-tip={`üí∞ ${trend.coin} - ${trend.platform}\nüß† ${trend.summary}`}
            />
          </Marker>
        ))}
      </ComposableMap>
      <ReactTooltip multiline={true} />
    </div>
  );
};

export default TrendMap;
