// src/components/Header.jsx
import React from "react";

const Header = ({ activeSection }) => {
  const scrollTo = (id) => {
    const el = document.getElementById(id);
    el?.scrollIntoView({ behavior: "smooth" });
  };

  const getBgColor = () => {
    switch (activeSection) {
      case "home":
        return "bg-black/40 backdrop-blur";
      case "crypto":
        return "bg-gradient-to-r from-blue-800 via-purple-900 to-indigo-800/80";
      case "video":
        return "bg-gradient-to-r from-green-700 via-emerald-900 to-teal-800/80";
      case "image":
        return "bg-gradient-to-r from-pink-800 via-red-900 to-fuchsia-700/80";
      default:
        return "bg-black/50";
    }
  };

  return (
    <header
      className={`fixed top-0 w-full z-50 text-white px-6 py-4 transition-all duration-500 ${getBgColor()} rounded-b-2xl shadow-lg`}
    >
      <nav className="flex justify-center gap-10 text-lg font-semibold tracking-wide">
        <button onClick={() => scrollTo("home")}>Home</button>
        <button onClick={() => scrollTo("crypto")}>Crypto Trend</button>
        <button onClick={() => scrollTo("video")}>Video Gen</button>
        <button onClick={() => scrollTo("image")}>Image Gen</button>
      </nav>
    </header>
  );
};

export default Header;
