import React, { useState } from 'react';

const videoCategories = [
  'Crypto Explainers',
  'AI in Finance',
  'Blockchain Tutorials',
  'Market Analysis',
  'NFT Showcase',
];

const generationTypes = [
  'Animated',
  'Ultra Realistic',
  '3D Rendered',
  'Abstract Art',
  'Futuristic Cyberpunk',
];

const topTrends = [
  'Bitcoin Rally',
  'Ethereum Merge',
  'AI Market Boom',
  'DeFi Innovations',
  'Metaverse Growth',
];

export default function VideoGen() {
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [selectedGenTypes, setSelectedGenTypes] = useState([]);
  const [prompt, setPrompt] = useState('');
  const [selectedTrends, setSelectedTrends] = useState([]);
  const [generatedVideo, setGeneratedVideo] = useState(null);

  const toggleSelection = (item, selectedList, setSelectedList) => {
    if (selectedList.includes(item)) {
      setSelectedList(selectedList.filter((i) => i !== item));
    } else {
      setSelectedList([...selectedList, item]);
    }
  };

  const generateVideo = () => {
    setGeneratedVideo('https://www.w3schools.com/html/mov_bbb.mp4');
  };

  return (
    <div className="max-w-5xl mx-auto space-y-8 p-6 md:p-8 bg-black/30 backdrop-blur-md rounded-3xl shadow-lg border border-cyan-600/30">
      <h2 className="text-4xl md:text-5xl font-extrabold text-cyan-400 drop-shadow-neon text-center">
        Video Generator
      </h2>

      {/* Categories Multi-select */}
      <section className="space-y-3">
        <h3 className="text-xl md:text-2xl font-semibold text-gray-300">Select Video Categories</h3>
        <div className="flex flex-wrap gap-3">
          {videoCategories.map((cat) => (
            <button
              key={cat}
              onClick={() => toggleSelection(cat, selectedCategories, setSelectedCategories)}
              className={`px-4 py-2 rounded-full border-2 cursor-pointer select-none transition-all duration-300
                ${
                  selectedCategories.includes(cat)
                    ? 'bg-cyan-500 border-cyan-400 text-black drop-shadow-neon'
                    : 'border-gray-700 text-gray-400 hover:border-cyan-400 hover:text-cyan-300'
                }`}
            >
              {cat}
            </button>
          ))}
        </div>
      </section>

      {/* Generation Types Multi-select */}
      <section className="space-y-3">
        <h3 className="text-xl md:text-2xl font-semibold text-gray-300">Select Generation Types</h3>
        <div className="flex flex-wrap gap-3">
          {generationTypes.map((type) => (
            <button
              key={type}
              onClick={() => toggleSelection(type, selectedGenTypes, setSelectedGenTypes)}
              className={`px-4 py-2 rounded-full border-2 cursor-pointer select-none transition-all duration-300
                ${
                  selectedGenTypes.includes(type)
                    ? 'bg-purple-600 border-purple-500 text-black drop-shadow-neon'
                    : 'border-gray-700 text-gray-400 hover:border-purple-500 hover:text-purple-400'
                }`}
            >
              {type}
            </button>
          ))}
        </div>
      </section>

      {/* Prompt Input */}
      <section className="space-y-3">
        <h3 className="text-xl md:text-2xl font-semibold text-gray-300">Enter a Prompt (Optional)</h3>
        <textarea
          placeholder="Describe your video idea..."
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          className="w-full p-4 rounded-2xl bg-gray-900 bg-opacity-70 text-white border border-gray-700 focus:border-cyan-400 outline-none resize-none shadow-lg"
          rows={4}
        />
      </section>

      {/* Top Trends Multi-select */}
      <section className="space-y-3">
        <h3 className="text-xl md:text-2xl font-semibold text-gray-300">Select Top Trends of the Day</h3>
        <div className="flex flex-wrap gap-3">
          {topTrends.map((trend) => (
            <button
              key={trend}
              onClick={() => toggleSelection(trend, selectedTrends, setSelectedTrends)}
              className={`px-4 py-2 rounded-full border-2 cursor-pointer select-none transition-all duration-300
                ${
                  selectedTrends.includes(trend)
                    ? 'bg-emerald-500 border-emerald-400 text-black drop-shadow-neon'
                    : 'border-gray-700 text-gray-400 hover:border-emerald-400 hover:text-emerald-300'
                }`}
            >
              {trend}
            </button>
          ))}
        </div>
      </section>

      {/* Generate Button */}
      <div className="text-center">
        <button
          onClick={generateVideo}
          className="px-10 py-3 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-full font-semibold text-lg hover:scale-105 transition-transform duration-300 shadow-neon"
        >
          Generate Video
        </button>
      </div>

      {/* Video Preview and Download */}
      {generatedVideo && (
        <section className="space-y-4 mt-6">
          <video
            src={generatedVideo}
            controls
            className="w-full rounded-3xl shadow-2xl border-2 border-cyan-400 drop-shadow-neon"
          />
          <div className="flex justify-center">
            <a
              href={generatedVideo}
              download="trendai_generated_video.mp4"
              className="px-8 py-2 border-2 border-cyan-400 rounded-full text-cyan-400 font-semibold
                hover:bg-cyan-400 hover:text-black transition-colors duration-300 drop-shadow-neon"
            >
              Download Video
            </a>
          </div>
        </section>
      )}
    </div>
  );
}
