import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icons
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerShadow from 'leaflet/dist/images/marker-shadow.png';

const CryptoMap = () => {
  const [trends, setTrends] = useState([]);

  useEffect(() => {
    fetch('/trendData.json')
      .then(res => res.json())
      .then(data => setTrends(data))
      .catch(err => console.error('Failed to load trend data:', err));
  }, []);

  const countryCoordinates = {
    US: [37.0902, -95.7129],
    JP: [36.2048, 138.2529],
    DE: [51.1657, 10.4515]
  };

  // Custom marker icon with shadow
  const customIcon = new L.Icon({
    iconUrl: markerIcon,
    shadowUrl: markerShadow,
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41]
  });

  return (
    <div className="relative w-full h-[90vh] rounded-2xl overflow-hidden border border-white/10 shadow-xl backdrop-blur-md bg-white/5">
      {/* Title */}
      <div className="absolute top-0 left-0 w-full text-center z-[1000] p-4 backdrop-blur-md bg-white/10 text-white text-xl font-semibold tracking-wide">
        üåç Trending Crypto Globally
      </div>

      <MapContainer center={[20, 0]} zoom={2} style={{ height: '100%', width: '100%' }} scrollWheelZoom={true}>
        <TileLayer
          url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
          attribution='&copy; OpenStreetMap contributors'
        />

        {trends.map((trend, idx) => {
          const position = countryCoordinates[trend.isoCode];
          if (!position) return null;

          return (
            <Marker key={idx} position={position} icon={customIcon}>
              <Popup className="text-sm">
                <strong>{trend.coin}</strong> on {trend.platform}<br />
                Sentiment: {trend.sentiment > 0 ? 'üìà Positive' : 'üìâ Negative'}<br />
                Mentions: {trend.mentions}<br />
                <em>{trend.snippet}</em><br />
                <img src={trend.top_meme} alt="meme" width="100%" style={{ marginTop: '10px' }} /><br />
                <video src={trend.top_video} controls width="100%" style={{ marginTop: '10px' }} />
              </Popup>
            </Marker>
          );
        })}
      </MapContainer>
    </div>
  );
};

export default CryptoMap;
