import { useState } from 'react';

export default function VideoGen() {
  const [category, setCategory] = useState('');
  const [style, setStyle] = useState('');
  const [prompt, setPrompt] = useState('');
  const [trend, setTrend] = useState('');
  const [videoURL, setVideoURL] = useState(null);

  const handleGenerate = () => {
    setVideoURL('https://via.placeholder.com/640x360?text=Generated+Video');
  };

  return (
    <div className="p-8 space-y-6">
      <h2 className="text-3xl font-semibold text-cyan-400">Video Generator</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <select className="bg-transparent border border-cyan-400 p-2 rounded" onChange={(e) => setCategory(e.target.value)}>
          <option value="">Select Crypto/AI Category</option>
          <option value="blockchain">Blockchain</option>
          <option value="defi">DeFi</option>
          <option value="machine-learning">Machine Learning</option>
        </select>
        <select className="bg-transparent border border-cyan-400 p-2 rounded" onChange={(e) => setStyle(e.target.value)}>
          <option value="">Select Video Style</option>
          <option value="animated">Animated</option>
          <option value="realistic">Ultra Realistic</option>
          <option value="cinematic">Cinematic</option>
        </select>
        <input type="text" placeholder="Optional prompt..." className="col-span-full bg-transparent border border-cyan-400 p-2 rounded" value={prompt} onChange={(e) => setPrompt(e.target.value)} />
        <select className="bg-transparent border border-cyan-400 p-2 rounded" onChange={(e) => setTrend(e.target.value)}>
          <option value="">Select Top Trend of the Day</option>
          <option value="bitcoin">Bitcoin Surge</option>
          <option value="eth-merge">ETH Merge</option>
          <option value="ai-bots">AI Bots</option>
        </select>
      </div>
      <button onClick={handleGenerate} className="bg-cyan-400 text-black px-4 py-2 rounded shadow hover:bg-cyan-300">Generate Video</button>
      {videoURL && (
        <div className="mt-6">
          <video src={videoURL} controls className="w-full max-w-3xl rounded-lg" />
          <a href={videoURL} download className="block mt-2 text-cyan-400 underline">Download Video</a>
        </div>
      )}
    </div>
  );
}
