import React from "react";
import "./Banner3DSlider.css";

/**
 * images can be strings (img/video paths) or objects:
 *   { src, type?, poster? }
 */
export default function Banner3DSlider({
  images = [],
  title = "",
  subtitle = "",
  authorTitle = "",
  authorRole = "",
  authorText = "",
  modelImage = null,       // legacy silhouette (optional)
  centerLogo = null,       // static centered logo (e.g. "/images/trendai_logo.png")
  logoSize,                // optional CSS size, e.g. "clamp(260px, 34vw, 460px)"
  showBackdrop = true,     // show the glow plate behind the cards
}) {
  const showContent =
    Boolean(title || subtitle || authorTitle || authorRole || authorText || modelImage);

  const items = images.map((e) => (typeof e === "string" ? { src: e } : e));
  const logoStyle = logoSize ? { ["--logo-size"]: logoSize } : undefined;

  return (
    <div className="banner relative">
      {/* Backplate behind the rotating ring */}
      {showBackdrop && <div className="ring-backdrop" aria-hidden="true" />}

      {/* Static centered logo (does not rotate) */}
      {centerLogo && (
        <div className="centerpiece" style={logoStyle} aria-hidden="true">
          <img src={centerLogo} alt="TrendAI logo" className="centerpiece-img" />
        </div>
      )}

      {/* 3D rotating ring */}
      <div className="slider" style={{ "--quantity": items.length }}>
        {items.map((item, i) => {
          const isVideo =
            item.type === "video" || /\.(mp4|webm|ogg)$/i.test(item.src);
          return (
            <div className="item" key={i} style={{ "--position": i + 1 }}>
              {isVideo ? (
                <video
                  className="media"
                  src={item.src}
                  poster={item.poster}
                  playsInline
                  muted
                  loop
                  autoPlay
                  preload="metadata"
                />
              ) : (
                <img className="media" src={item.src} alt={`slide-${i + 1}`} />
              )}
            </div>
          );
        })}
      </div>

      {/* Optional content layer */}
      {showContent && (
        <div className="content">
          <h1 data-content={title}>{title}</h1>
          <div className="author">
            <h2>{authorTitle}</h2>
            <p><b>{authorRole}</b></p>
            <p>{authorText}</p>
            <p style={{ marginTop: 10, opacity: 0.8 }}>{subtitle}</p>
          </div>
          {!!modelImage && (
            <div className="model" style={{ backgroundImage: `url(${modelImage})` }} />
          )}
        </div>
      )}
    </div>
  );
}
