import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { useEffect, useState } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import './CryptoMap.css'; // optional, for better control

// Fix: Ensure Leaflet's default marker images are correctly loaded
import markerIcon2x from 'leaflet/dist/images/marker-icon-2x.png';
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerShadow from 'leaflet/dist/images/marker-shadow.png';

// Set the default icon globally
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

const CryptoMap = () => {
  const [trends, setTrends] = useState([]);

  useEffect(() => {
    fetch('/trendData.json')
      .then(res => res.json())
      .then(data => setTrends(data))
      .catch(err => console.error('Failed to load trend data:', err));
  }, []);

  const countryCoordinates = {
    US: [37.0902, -95.7129],
    JP: [36.2048, 138.2529],
    DE: [51.1657, 10.4515]
    // Add more countries as needed
  };

  return (
    <div style={{
      position: 'relative',
      borderRadius: '20px',
      overflow: 'hidden',
      margin: '30px',
      background: 'rgba(10, 10, 25, 0.7)', // softer translucent
      boxShadow: '0 0 15px rgba(0, 255, 255, 0.1)',
      backdropFilter: 'blur(12px)'
    }}>
      <h2 style={{
        color: '#00ffee',
        textAlign: 'center',
        padding: '20px 0 10px',
        fontSize: '1.5rem',
        fontWeight: '600'
      }}>
        üåç Trending Crypto Activity Worldwide
      </h2>

      <MapContainer center={[20, 0]} zoom={2} style={{ height: '80vh', width: '100%' }}>
        <TileLayer
          url='https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
          attribution='&copy; OpenStreetMap contributors'
        />

        {trends.map((trend, idx) => (
          <Marker
            key={idx}
            position={countryCoordinates[trend.isoCode]}
          >
            <Popup>
              <strong>{trend.coin}</strong> on {trend.platform}<br />
              Sentiment: {trend.sentiment > 0 ? 'üìà Positive' : 'üìâ Negative'}<br />
              Mentions: {trend.mentions}<br />
              <em>{trend.snippet}</em><br />
              {trend.top_meme && (
                <img src={trend.top_meme} alt="meme" width="100%" style={{ marginTop: '10px' }} />
              )}
              {trend.top_video && (
                <video src={trend.top_video} controls width="100%" style={{ marginTop: '10px' }} />
              )}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default CryptoMap;
