import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

// Fixing missing marker icon issue in Leaflet (Webpack issue)
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png",
  iconUrl: "https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png",
  shadowUrl: "https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png",
});

// Example crypto trend data (replace with real API data later)
const cryptoTrendLocations = [
  {
    name: "New York",
    trend: "Bitcoin Surge",
    position: [40.7128, -74.006],
  },
  {
    name: "London",
    trend: "Ethereum Volume Spike",
    position: [51.5072, -0.1276],
  },
  {
    name: "Tokyo",
    trend: "Solana Rising",
    position: [35.6895, 139.6917],
  },
  {
    name: "Dubai",
    trend: "Altcoins Buzzing",
    position: [25.2048, 55.2708],
  },
];

const CryptoMap = () => {
  return (
    <div className="h-[500px] rounded-2xl overflow-hidden shadow-2xl">
      <MapContainer
        center={[20, 0]}
        zoom={2}
        scrollWheelZoom={true}
        style={{ height: "100%", width: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        />
        {cryptoTrendLocations.map((location, index) => (
          <Marker key={index} position={location.position}>
            <Popup>
              <strong>{location.name}</strong>
              <br />
              Trend: {location.trend}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default CryptoMap;
